      \fill[seq4!10, nearly opaque] (-1,-1) rectangle (1,1);
      \draw[step=.5cm, seq4!30, very thin] (-1,-1) grid (1,1);
      \point["$Ax$" {fill=none,below}] (Q) at (-.5,.5);
